<?phpfunction get_users_selection_options($selected, $disabled, $order_by, $asc_or_desc, $deleted)	{		/* 			Отримання випадаючого списку користувачів				До функції:					$disabled = array(); або $disabled = (1, 3, 8); - відмічаєм блокованих користувачів, це масів.					$selected = 0; - не ставити вибраного користувача, $selected = 5; - вибрати користувача ID = 5.					$order_by = "name"; - сорутвати по імені, id - по ІД.					$asc_or_desc = "DESC"; - сортувати за спаданням Я-А, ASC - зростання А-Я.					$deleted:						0 - показати всіх не вилучених						1 - показати всіх вилучених						-1 - показати всіх				Визов ф-ції:						$disabled = 0; (вимкнути блокування)						$disabled = array(1, 3); (блокування ID-1 та ID-3) 											$users = get_users_selection_options(5, 0, "name", "ASC", 0);					вибрати із списку користувача з ID=5, нікого не блокувати, сортувати по імені із зростанням А-Я., показати всіх не вилучених.		*/				$where_del = " WHERE `del`='0' ";		if ($deleted == -1) $where_del = "";		if ($deleted == 1) $where_del = " WHERE `del`='1' ";		$asc_desc = "ASC";		if ($asc_or_desc == "DESC") $asc_desc = "DESC";				$query = "SELECT * FROM `users` ".$where_del." ORDER BY `".$order_by."` ".$asc_desc." ; ";		$res = mysql_query($query) or die(mysql_error());		$html = "";		while ($row=mysql_fetch_array($res))			{				$is_selected = "";				if ($selected == $row['id']) $is_selected = "selected";				$is_disabled = "";				if ($disabled != 0 AND in_array($row['id'], $disabled)) $is_disabled = "disabled";				$html .= "<option value=\"".$row['id']."\" ".$is_selected." ".$is_disabled.">".$row['name']."</option>\n";				$row['id'] = $row['name'];			}		return $html;	}function check_data($data)	{		// Перевірка дати по календарю if (check_data(2015-12-31)) die("Все супер");		// Можна з часом if (check_data(2015-12-31 23:59:59)) die("Все супер");		$result = true;		$tmp = explode(" ", $data);		if (!empty($tmp[1]) AND !preg_match("/^(0[1-9]|1[0-9]|2[0-3]):(0[1-9]|[0-5][0-9]):(0[1-9]|[0-5][0-9])$/", $tmp[1])) $result = false;		$data = explode("-", $tmp[0]);		if (!preg_match("/^([1-9][0-9]{3}|[2-9]{1}[0-9]{3})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1])$/" ,$tmp[0])) $result = false;		$data = explode("-", $tmp[0]);		if (!checkdate($data[1], $data[2], $data[0])) $result = false;		return $result;	}?>